mapper.select:
SELECT us.id AS id, us.login_name AS loginName, us.NAME AS 'name', us.phone AS phone, us.email AS email, us.user_type AS userType, us.STATUS AS status, us.institution_id AS institutionId, us.create_time AS createTime, GROUP_CONCAT(ro.name) AS 'rolesList' FROM models_user us LEFT JOIN models_user_role ur ON us.id = ur.user_id LEFT JOIN models_role ro ON ur.role_id = ro.id
us.name = #{name}
and us.create_time >=#{startTime}
and us.create_time <=#{endTime}
GROUP BY us.id

SELECT us.id AS id, us.login_name AS loginName, us.password AS password, us.NAME AS name, us.phone AS phone, us.email AS email, us.STATUS AS status, us.create_time AS createTime, ro.name AS roleName, us.is_del AS isDel from models_user us LEFT JOIN models_user_role ur ON us.id = ur.user_id LEFT JOIN models_role ro ON ur.role_id = ro.id
us.is_del = 0
and us.STATUS = #{userStatus}
and ro.id = #{userRole}
and ( us.NAME like concat('%',#{keyWord},'%') or us.login_name like concat('%',#{keyWord},'%') or ro.name like concat('%',#{keyWord},'%') )
order by us.create_time desc

select

from models_user
where is_del = 0 and login_name = #{loginName,jdbcType=VARCHAR}

select

from models_user
where is_del = 0 and login_name = #{loginName,jdbcType=VARCHAR}

mapper.update:
update models_user
set status = 1
where id = #{id,jdbcType=BIGINT}

update models_user
set status = 0
where id = #{id,jdbcType=BIGINT}

update models_user
set status = 2
where id = #{id,jdbcType=BIGINT}

update models_user
set is_del = 1
where id = #{id,jdbcType=BIGINT}
